import React, { useState } from "react";
import Sidebar from "./Sidebar.jsx";
import UserManagement from "./UserManagement.jsx";
import OrderManagement from "./OrderManagement.jsx";
import TicketManagement from "./TicketManagement.jsx";
import PromotionManagement from "./PromotionManagement.jsx";
import Statistics from "./Statistics.jsx";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [page, setPage] = useState("statistics");

  const handleLogout = () => {
    // Điều hướng về trang đăng nhập
    window.location.href = "/login"; // Thay "/login" bằng đường dẫn thực tế của giao diện đăng nhập
  };

  const renderPage = () => {
    switch (page) {
      case "statistics":
        return <Statistics />;
      case "users":
        return <UserManagement />;
      case "orders":
        return <OrderManagement />;
      case "tickets":
        return <TicketManagement />;
      case "promotions":
        return <PromotionManagement />;
      default:
        return <Statistics />;
    }
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        <button onClick={() => setPage("statistics")}>Thống kê</button>
        <button onClick={() => setPage("users")}>Quản lý Người dùng</button>
        <button onClick={() => setPage("orders")}>Quản lý Đơn hàng</button>
        <button onClick={() => setPage("tickets")}>Quản lý Vé</button>
        <button onClick={() => setPage("promotions")}>
          Quản lý Khuyến mãi
        </button>
        <button className="logout-btn" onClick={handleLogout}>
          Đăng xuất
        </button>
      </div>
      <div className="content">{renderPage()}</div>
    </div>
  );
};

export default Dashboard;
