import React from "react";
import { Link } from "react-router-dom";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const handleLogout = () => {
    // Điều hướng về trang đăng nhập
    window.location.href = "/login"; // Thay "/login" bằng đường dẫn thực tế của giao diện đăng nhập
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        <Link to="/admin/dashboard">Thống kê</Link>
        <Link to="/admin/users">Quản lý Người dùng</Link>
        <Link to="/admin/flights">Quản lý Chuyến bay</Link>
        <Link to="/admin/orders">Quản lý Đơn đặt vé</Link>
        <Link to="/admin/coupons">Quản lý Khuyến mãi</Link>
        <button className="logout-btn" onClick={handleLogout}>
          Đăng xuất
        </button>
      </div>
      <div className="content">
        {/* Nội dung sẽ được hiển thị dựa trên router */}
      </div>
    </div>
  );
};

export default Dashboard;
