import React, { useEffect, useState } from "react";
import api from "../services/api.js";
import "../styles/PromotionManagement.css";

const PromotionManagement = () => {
  const [promotions, setPromotions] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [newPromo, setNewPromo] = useState({
    name: "",
    code: "",
    discount: "",
    expiry: "",
  });

  // Lấy danh sách khuyến mãi từ API
  useEffect(() => {
    api.get("/promotions").then((res) => setPromotions(res.data));
  }, []);

  // Xóa khuyến mãi
  const handleDelete = async (id) => {
    await api.delete(`/promotions/${id}`);
    setPromotions(promotions.filter((p) => p._id !== id));
  };

  // Mở/Đóng modal
  const toggleModal = () => {
    setShowModal(!showModal);
    if (showModal) {
      // Reset form khi đóng modal
      setNewPromo({ name: "", code: "", discount: "", expiry: "" });
    }
  };

  // Xử lý thay đổi input trong form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPromo({ ...newPromo, [name]: value });
  };

  // Thêm khuyến mãi mới
  const handleAddPromo = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post("/promotions", newPromo);
      setPromotions([...promotions, response.data]);
      toggleModal(); // Đóng modal sau khi thêm
    } catch (error) {
      console.error("Error adding promotion:", error);
    }
  };

  return (
    <div className="container">
      <h2>Quản lý Khuyến mãi</h2>
      <button className="add-btn" onClick={toggleModal}>
        Thêm Khuyến mãi
      </button>

      {/* Modal để thêm khuyến mãi */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <button className="modal-close" onClick={toggleModal}>
              ×
            </button>
            <form onSubmit={handleAddPromo}>
              <div className="form-group">
                <label>Tên khuyến mãi:</label>
                <input
                  type="text"
                  name="name"
                  value={newPromo.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Mã:</label>
                <input
                  type="text"
                  name="code"
                  value={newPromo.code}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Giảm giá (%):</label>
                <input
                  type="number"
                  name="discount"
                  value={newPromo.discount}
                  onChange={handleInputChange}
                  required
                  min="0"
                  max="100"
                />
              </div>
              <div className="form-group">
                <label>Ngày hết hạn:</label>
                <input
                  type="date"
                  name="expiry"
                  value={newPromo.expiry}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <button type="submit" className="submit-btn">
                Thêm Khuyến mãi
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Bảng danh sách khuyến mãi */}
      <table>
        <thead>
          <tr>
            <th>Tên khuyến mãi</th>
            <th>Mã</th>
            <th>Giảm (%)</th>
            <th>Ngày hết hạn</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {promotions.map((promo) => (
            <tr key={promo._id}>
              <td>{promo.name}</td>
              <td>{promo.code}</td>
              <td>{promo.discount}%</td>
              <td>{new Date(promo.expiry).toLocaleDateString()}</td>
              <td>
                <button
                  className="action-btn delete-btn"
                  onClick={() => handleDelete(promo._id)}
                >
                  Xoá
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PromotionManagement;
