import React, { useState, useEffect } from "react";
import axios from "axios";

function OrdersManagement() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    axios
      .get("/api/orders") // Giả sử bạn có API để lấy danh sách đơn đặt hàng
      .then((response) => setOrders(response.data))
      .catch((error) =>
        console.error("Có lỗi khi lấy dữ liệu đơn đặt hàng:", error)
      );
  }, []);

  return (
    <div>
      <h2>Quản lý Đơn đặt hàng</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Người đặt</th>
            <th>Trạng thái</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order.id}>
              <td>{order.id}</td>
              <td>{order.customerName}</td>
              <td>{order.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default OrdersManagement;
