import React, { useState, useEffect } from "react";
import axios from "axios";

function TicketsManagement() {
  const [tickets, setTickets] = useState([]);

  useEffect(() => {
    axios
      .get("/api/tickets") // Giả sử bạn có API để lấy danh sách vé
      .then((response) => setTickets(response.data))
      .catch((error) =>
        console.error("Có lỗi khi lấy dữ liệu vé máy bay:", error)
      );
  }, []);

  return (
    <div>
      <h2>Quản lý Vé máy bay</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Chuyến bay</th>
            <th>Giá</th>
          </tr>
        </thead>
        <tbody>
          {tickets.map((ticket) => (
            <tr key={ticket.id}>
              <td>{ticket.id}</td>
              <td>{ticket.flight}</td>
              <td>{ticket.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TicketsManagement;
